var whitespace = " \t\n\r";
function isEmpty(s) {
 if((s == null) || (s.length == 0)) {
	return true;
 }
 for (var i = 0; i < s.length; i++) {   
  var c = s.charAt(i);
  if (whitespace.indexOf(c) == -1) {
	return false;
  }
 }
 return true;
}

